import React, { Component } from "react";
import Slider from "react-slick";
import url from "../link-data";

export default class Sanphammoi extends Component {
  constructor(props) {
    super(props);
    this.state = {
      db: []
    };
  }
  componentDidMount() {
    fetch(`${url}`)
      .then(result => {
        return result.json();
      })
      .then(data =>
        this.setState({
          db: data
        })
      );
  }
  render() {
    var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 4,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    const { db } = this.state;
    const list = db.map((demo, index) => (
      <div key={index} className='sanphammoi'>
        <img src={demo.src} className='item-sanphammoi'/>
        <span className='name'>{demo.ten}</span>
        <br/>
        <span className='gia'>{demo.gia.toLocaleString()}đ</span>
      </div>
    ));
    return (
      <div>
        <div className="category">
          <button className="btn-category">SẢN PHẨM MỚI NHẤT</button>
          <button className="btn-category">SẢN PHẨM BÁN CHẠY</button>
          <button className="btn-category">SẢN PHẨM PHỔ BIẾN</button>
        </div>
        <Slider {...settings}>{list}</Slider>
      </div>
    );
  }
}
