import React, { Component } from "react";
import Slider from "react-slick";
import url from "../link-data";

export default class Sanphammoi extends Component {
  constructor(props) {
    super(props);
    this.state = {
      db: ""
    };
  }
  async componentDidMount() {
    const res = await fetch(`${url}`);
    const data = await res.json();
    this.setState({
      db: data
    });
    console.log(this.state.db);
  }

  render() {
    var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 4,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    return (
      <div>
        <div className="category">
          <button className="btn-category">SẢN PHẨM MỚI NHẤT</button>
          <button className="btn-category">SẢN PHẨM BÁN CHẠY</button>
          <button className="btn-category">SẢN PHẨM PHỔ BIẾN</button>
        </div>
        <Slider {...settings}>
          <div>
            <h3>1</h3>
          </div>
          <div>
            <h3>2</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
          <div>
            <h3>7</h3>
          </div>
          <div>
            <h3>8</h3>
          </div>
        </Slider>
      </div>
    );
  }
}
